import { HttpClient, HttpHeaders } from "@angular/common/http";
import { Inject, NgModule } from "@angular/core";
import { Injectable } from "@angular/core";
import { catchError, lastValueFrom, map, Observable, of } from "rxjs";
import { __values } from "tslib";
import { Employee } from "./Employee";


export interface EmployeesResponse {
  data: Employee[];
  // Add other backend response properties here if present
}

@Injectable({ providedIn: 'root' })
export class EmployeeService {
  employees: Employee[] = [];
  http: HttpClient;
  baseUrl: string;
  httpOptions = {
     headers: new HttpHeaders({'ContentType': 'application/json'})
  };

  constructor(http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
    this.http = http;
    this.baseUrl = baseUrl;

  }

  ngOnInit() {
    const apiURL = this.baseUrl + 'Employee';

    this.http.get<Employee[]>(apiURL 
    ).subscribe(result => {
      this.employees = result;
      console.log(result);
      console.log(this.employees);
    }, error => console.error(error));

  }

  getEmployees(): Observable<Employee[]> {
    const apiURL = this.baseUrl + 'Employee';
   
    return this.http.get<EmployeesResponse>(apiURL).pipe(
       map((res: EmployeesResponse) => res.data)
    ); 
  }

  refreshEmployees(): Observable<Currency[]> {
    return this.http.get<EmployeesResponse>('api/refresh').pipe(
      map((res: EmployeesResponse) => res.data)
    );
  }

  work(days: number, id: number): Observable<Employee[]> {
    this.http.post<Employee[]>(this.baseUrl + 'Employee'+ '\\Work', { days: days, id: id }
      ).subscribe(result => {
        this.employees = result;
      }, error => console.error(error));

    return this.employees;
  }

  vacation(days: number, id: number) {
    this.http.post<Employee[]>(this.baseUrl + 'Employee' + '\\TakeVacation', { days: days, id: id }
    ).subscribe(result => {
      this.employees = result;
    }, error => console.error(error));

    return this.employees;
  }

}
