import { Component, Inject } from '@angular/core';
import { FormBuilder, FormGroup, Validators, FormControl } from '@angular/forms';
import { Employee } from '../Employee';
import { EmployeeService } from '../employee.service';


@Component({
  selector: 'app-fetch-data',
  templateUrl: './fetch-data.component.html'
})
export class FetchDataComponent {
  public employees: Employee[] = [];
  private service: EmployeeService;
  //Form Validables
  public modalId: number = 0;
  displayWorkModal: boolean = false;
  Work!: FormGroup;
  Vacation!: FormGroup;

  DaysWorked!: FormControl;
  VacationDays!: FormControl;
  submitted = false;

  constructor(private formBuilder: FormBuilder, service:EmployeeService) {
    this.service = service;
  }

  get f() { return this.Work.controls; }
  onSubmitWork() {
    if (this.Work.valid) {
      /* write your code here */
      const days = this.Work.get('DaysWorked')?.value;
      console.log(days);
      this.submitted = true;
      // stop here if form is invalid
      if (this.Work.invalid) {

        return;
      }
      //True if all the fields are filled
      if (this.submitted) {

        this.work(days, this.modalId);
      }
    }
    else {
      alert('Invalid Input');
    }

  }

  get v() { return this.Vacation.controls; }
  onTakeVacation() {
    if (this.Vacation.valid) {
      const days = this.Vacation.get('VacationDays')?.value;
      console.log(days);
      this.submitted = true;
      // stop here if form is invalid
      if (this.Vacation.invalid) {
        return;
      }
      //True if all the fields are filled
      if (this.submitted) {
        this.takeVacation(days, this.modalId);
      }
    }
    else {
      alert('Invalid Input');
    }
  }
  ngOnInit() {
    //Add User form validations
    this.service.getEmployees().subscribe(
      data => this.employees = data
    );
    this.Work = this.formBuilder.group({
      DaysWorked: ['', [Validators.required, Validators.max(260), Validators.min(0)]],
    });

    this.Vacation = this.formBuilder.group({
      VacationDays: ['', [Validators.required, Validators.max(30), Validators.min(0)]],
    });
    
  }

  prepDialog(id: number) {
    this.modalId = id;

    console.log(this.employees[id].maxWorkDays);
    
    
    this.Work = this.formBuilder.group({
      DaysWorked: ['', [Validators.required, Validators.max(this.employees[id].maxWorkDays), Validators.min(0)]],
    });

    console.log(this.Work);
    //this.Vacation = this.formBuilder.group({
    //  VacationDays: ['', [Validators.required, Validators.max(this.employees[id].maxVacationDays), Validators.min(0)]],
    //});
  }

  public work(days: number, id: number) {

    this.service.work(days, id).subscribe(
      (data) => { this.employees = data },
      (error) => { console.log(error); }
    );
  }

  public takeVacation(days:number, id: number) {
    this.service.vacation(days, id).subscribe(
      (data) => { this.employees = data },
      (error) => {console.log(error) }
    );
  }

}

<h1 id="tableLabel">Employees</h1>

<p>Sample Employees for exercise</p>

<!-- Modal -->
<div class="modal fade" id="WorkModal" tabindex="-1" aria-labelledby="WorkModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title text-danger" id="WorkModalLabel">Work Entry Form</h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        <form [formGroup]="Work" (ngSubmit)="onSubmitWork()">
          <div class="mb-3">
            <label for="DaysWorked" class="form-label">Days Worked</label>
            <input type="number" formControlName="DaysWorked" class="form-control" [ngClass]="{ 'is-invalid': submitted && f.DaysWorked.errors }" id="DaysWorked">
            <div *ngIf="submitted && f.DaysWorked.errors" class="invalid-feedback">
              <div *ngIf="f.DaysWorked.errors.required">Days Worked is required</div>
            </div>
          </div>

          <button type="submit" class="btn btn-primary" data-bs-dismiss="modal" >Submit</button>
        </form>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-warning" data-bs-dismiss="modal">Close</button>
      </div>
    </div>
  </div>
</div>

<!-- Modal -->
<div class="modal fade" id="TakeVacationModal" tabindex="-1" aria-labelledby="TakeVacationModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title text-danger" id="TakeVacationModalLabel">Vacation Day Entry Form</h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        <form [formGroup]="Vacation" (ngSubmit)="onTakeVacation()">
          <div class="mb-3">
            <label for="VacationDays" class="form-label">Vacation Days to use</label>
            <input type="number" formControlName="VacationDays" class="form-control" [ngClass]="{ 'is-invalid': submitted && v.VacationDays.errors }" id="VacationDays">
            <div *ngIf="submitted && v.VacationDays.errors" class="invalid-feedback">
              <div *ngIf="v.VacationDays.errors.required"> VacationDays is required</div>
            </div>
          </div>

          <button type="submit" class="btn btn-primary" data-bs-dismiss="modal">Submit</button>
        </form>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-warning" data-bs-dismiss="modal">Close</button>
      </div>
    </div>
  </div>
</div>

<p *ngIf="!employees?.length"><em>Loading...</em></p>


<table class='table table-striped' aria-labelledby="tableLabel" *ngIf="employees.length">
  <thead>
    <tr>
      <th>Id</th>
      <th>Name</th>
      <th>Type</th>
      <th>Vacation days</th>
      <th>Days Worked</th>
      <th>Max Work Days</th>
      <th>Max Vacation days</th>
      <th></th>
      <th></th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let emp of employees">
      <td>{{ emp.id }}</td>
      <td>{{ emp.name }}</td>
      <td>{{ emp.employeeType }}</td>
      <td>{{ emp.vacationDays.toFixed(2)}}</td>
      <td>{{ emp.daysWorked }}</td>
      <td>{{emp.maxWorkDays}}</td>
      <td>{{emp.maxVacationDays}}</td>
     
      <td><button mat-raised-button data-bs-toggle="modal" data-bs-target="#WorkModal" (click)="prepDialog(emp.id)">Work</button></td>
      <td><button mat-raised-button data-bs-toggle="modal" data-bs-target="#TakeVacationModal" (click)="prepDialog(emp.id)">Take Vacation</button></td>

    </tr>

  </tbody>
</table>
