import { HttpClient } from "@angular/common/http";
import { Inject, NgModule } from "@angular/core";
import { Injectable } from "@angular/core";
import { Employee } from "./Employee";


@Injectable({ providedIn: 'root' })


export class EmployeeService {
  employees: Employee[] = [];
  http: HttpClient;
  baseUrl: string;

  constructor(http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
    this.http = http;
    this.baseUrl = baseUrl;

  }

  ngOnInit() {
    this.employees = this.getEmployees();
  }

  getEmployees(): any {
    const apiURL = this.baseUrl + 'Employee';
    let res: Employee[];

    this.http.get<Employee[]>(apiURL).subscribe((response) => {
      console.log("Response " + res);
      return res;
    },
      error => console.log(error));

    //const promise = new Promise<void>((resolve, reject) => {
    //  
    //  this.http.get<Employee[]>(apiURL).subscribe({
    //    next: (res: any) => {
    //      this.employees = res.map((res: any) => {
    //        return res;
    //      });
    //      resolve();
    //    },
    //    error: (err: any) => {
    //      reject(err);
    //    },
    //    complete: () => {
    //      console.log('complete');
    //      return this.employees;
    //    },
    //  });
    //});
    //return promise;
  }

  //getEmployees(): any {
    
  //  const promise = this.http.get<Employee[]>().toPromise();
  //  console.log(promise);
  //  promise.then((data) => {
  //    return data;
  //  }).catch((error) => {
  //    console.log(error);
  //  })

  //}

  work(days: number, id: number) {
    this.http.post<Employee[]>(this.baseUrl + 'Employee'+ '\\Work', { days: days, id: id }
      ).subscribe(result => {
        this.employees = result;
      }, error => console.error(error));

    return this.employees;
  }

  vacation(days: number, id: number) {
    this.http.post<Employee[]>(this.baseUrl + 'Employee' + '\\TakeVacation', { days: days, id: id }
    ).subscribe(result => {
      this.employees = result;
    }, error => console.error(error));

    return this.employees;
  }

}
